	CIQ.monthAbv=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];

	/* Enumerated types for time units */
	CIQ.MILLISECOND=1;
	CIQ.SECOND=1000;
	CIQ.MINUTE=60*CIQ.SECOND;
	CIQ.HOUR=60*CIQ.MINUTE;
